
public class Operatoren1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Was sind Operatoren?
		/*
		 * Mit Operatoren kann man Variablen verÃ¤ndern oder allgemein
		 * mit ihnen arbeiten
		 */
		
		int varZahl = 5;
		
		int zahl1 = 10;
		int zahl2 = 5;
		
		int ergebnis;
		
		ergebnis = zahl1 + zahl2; //15
		System.out.println("Das Ergebnis ist: " + ergebnis);
		ergebnis = zahl1 - zahl2; //15
		System.out.println("Das Ergebnis ist: " + ergebnis);
		ergebnis = zahl1 * zahl2; //15
		System.out.println("Das Ergebnis ist: " + ergebnis);
		ergebnis = zahl1 / zahl2; //15
		System.out.println("Das Ergebnis ist: " + ergebnis);
		
		varZahl++;
		System.out.println("Das Ergebnis ist: " + varZahl);
		varZahl--;
		System.out.println("Das Ergebnis ist: " + varZahl);

	}

}
